"use strict";

/************
 * Requires *
 ************/

var FileMessage = require("./file_message");

/**************************
 * The AudioMessage class *
 **************************/

/**
 * A message which contains a downloadable audio file
 *
 * @extends FileMessage
 */
class AudioMessage extends FileMessage {
	/**
	 * Creates a new AudioMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The [Audio]{@link https://core.telegram.org/bots/api#audio} object this message contains
	 *
	 * @type {Object}
	 * @property {String} file_id	ID of the audio file
	 * @property {Integer} duration	Duration of the audio file
	 * @property {String} [performer]	The performer of the audio file
	 * @property {String} [title]	Title of the audio
	 * @property {String} [mime_type]	Mime type of the audio
	 * @property {Integer} [file_size]	Size of the audio, in bytes
	 * @readonly
	 */
	get audio() {
		return this.rawMessage.audio;
	}
}

/***********
 * Exports *
 ***********/

module.exports = AudioMessage;
