"use strict";

/************
 * Requires *
 ************/

var Message = require("./message");

/*********************************
 * The ChatMigratedMessage class *
 *********************************/

/**
 * A message which indicates a group chat has been migrated to a supergroup chat
 *
 * @extends Message
 */
class ChatMigratedMessage extends Message {
	/**
	 * Creates a new ChatMigratedMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The ID of the group chat which was migrated from
	 *
	 * @type {Integer}
	 * @readonly
	 */
	get from() {
		return this.rawMessage.migrate_from_chat_id;
	}

	/**
	 * The ID of the supergroup chat which was migrated from
	 *
	 * @type {Integer}
	 * @readonly
	 */
	get from() {
		return this.rawMessage.migrate_to_chat_id;
	}
}

/***********
 * Exports *
 ***********/

module.exports = ChatMigratedMessage;
