"use strict";

/************
 * Requires *
 ************/

var Message = require("./message");

/****************************
 * The ContactMessage class *
 ****************************/

/**
 * A message which contains a contact
 *
 * @extends Message
 */
class ContactMessage extends Message {
	/**
	 * Creates a new ContactMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The contact in this message, as a [Contact]{@link https://core.telegram.org/bots/api#contact} object
	 *
	 * @type {Object}
	 * @property {String} phone_number	The phone number of the contact
	 * @property {String} first_name	The first name of the contact
	 * @property {String} [last_name]	The last name of the contact
	 * @property {Integer} [user_id]	The user id of the contact
	 * @readonly
	 */
	get contact() {
		return this.rawMessage.contact;
	}
}

/***********
 * Exports *
 ***********/

module.exports = ContactMessage;
