"use strict";

/************
 * Requires *
 ************/

var TextMessage = require("./text_message");

/****************************
 * The ForwardMessage class *
 ****************************/

/**
 * A message which contains a forwarded message
 *
 * @extends TextMessage
 */
class ForwardedMessage extends TextMessage {
	/**
	 * Creates a new ForwardedMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The original sender of the message, not the one who forwarded it
	 *
	 * @type {Object}
	 * @readonly
	 */
	get originalSender() {
		return this.rawMessage.forward_from;
	}

	/**
	 * The timestamp of the original message, not this one
	 *
	 * @type {Integer}
	 * @readonly
	 */
	get originalDate() {
		return this.rawMessage.forward_date;
	}

}

/***********
 * Exports *
 ***********/

module.exports = ForwardedMessage;
