"use strict";

/************
 * Requires *
 ************/

var Message = require("./message");

/*****************************
 * The LocationMessage class *
 *****************************/

/**
 * A message which contains a location
 *
 * @extends Message
 */
class LocationMessage extends Message {
	/**
	 * Creates a new LocationMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The location in this message, as a latitude and longitude
	 *
	 * @type {Object}
	 * @property {Float} latitude	Latitude of the position
	 * @property {Float} longitude	Longitude of the position
	 * @readonly
	 */
	get location() {
		return this.rawMessage.location;
	}
}

/***********
 * Exports *
 ***********/

module.exports = LocationMessage;
