"use strict";

/************
 * Requires *
 ************/

var Message = require("./message");

/***************************************
 * The NewChatParticipantMessage class *
 ***************************************/

/**
 * A message which contains a user who just entered the chat. May be the bot itself
 *
 * @extends Message
 */
class NewChatParticipantMessage extends Message {
	/**
	 * Creates a new NewChatParticipantMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The user who just joined the chat
	 *
	 * @type {Object}
	 * @property {Integer} id	ID of the user
	 * @property {String} first_name	First name of the user
	 * @property {String} [last_name]	Last name of the user
	 * @property {String} [username]	Username of the user
	 * @readonly
	 */
	get user() {
		return this.rawMessage.new_chat_participant;
	}

}

/***********
 * Exports *
 ***********/

module.exports = NewChatParticipantMessage;
