"use strict";

/************
 * Requires *
 ************/

var Message = require("./message");

/*********************************
 * The NewChatTitleMessage class *
 *********************************/

/**
 * A message which contains the new title of the chat
 *
 * @extends Message
 */
class NewChatTitleMessage extends Message {
	/**
	 * Creates a new NewChatTitleMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The new title of the chat
	 *
	 * @type {String}
	 * @readonly
	 */
	get title() {
		return this.rawMessage.new_chat_title;
	}

}

/***********
 * Exports *
 ***********/

module.exports = NewChatTitleMessage;
