"use strict";

/************
 * Requires *
 ************/

var Message = require("./message");

/*************************
 * The TextMessage class *
 *************************/

/**
 * A message which contains text
 *
 * @extends Message
 */
class TextMessage extends Message {
	/**
	 * Creates a new TextMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The text of the message
	 *
	 * @type {String}
	 * @readonly
	 */
	get text() {
		return this.rawMessage.text;
	}

}

/***********
 * Exports *
 ***********/

module.exports = TextMessage;
