"use strict";

/************
 * Requires *
 ************/

var FileMessage = require("./file_message");

/**************************
 * The VideoMessage class *
 **************************/

/**
 * A message which contains a downloadable video file
 *
 * @extends FileMessage
 */
class VideoMessage extends FileMessage {
	/**
	 * Creates a new VideoMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The [Video]{@link https://core.telegram.org/bots/api#video} object this message contains
	 *
	 * @type {Object}
	 * @property {String} file_id	ID of the video
	 * @property {Integer} width	Width of the video, in pixels
	 * @property {Integer} height	Height of the video, in pixels
	 * @property {Integer} duration	Duration of the video
	 * @property {Object} [thumb]	The thumb of the video, as a [PhotoSize]{@link https://core.telegram.org/bots/api#photosize} object
	 * @property {String} [mime_type]	Mime type of the video
	 * @property {Integer} [file_size]	Size of the video, in bytes
	 * @readonly
	 */
	get video() {
		return this.rawMessage.video;
	}
}

/***********
 * Exports *
 ***********/

module.exports = VideoMessage;
