"use strict";

/************
 * Requires *
 ************/

var FileMessage = require("./file_message");

/*****************************
 * The VoiceMessage class *
 *****************************/

/**
 * A message which contains a downloadable voice file
 *
 * @extends FileMessage
 */
class VoiceMessage extends FileMessage {
	/**
	 * Creates a new VoiceMessage object
	 *
	 * @param {Object}Â message	A raw message object from an update
	 * @param {BotAPI} API	An instance of a BotAPI from teleapiwrapper which will be used to do stuff with this message
	 *
	 * @private
	 */
	constructor(message, API) {
		// Call the super constructor
		super(message, API);
	}

	/**
	 * The [Voice]{@link https://core.telegram.org/bots/api#voice} object this message contains
	 *
	 * @type {Object}
	 * @property {String} file_id	ID of the voice file
	 * @property {Integer} duration	Duration of the voice file
	 * @property {String} [mime_type]	Mime type of the voice file
	 * @property {Integer} [file_size]	Size of the voice file, in bytes
	 * @readonly
	 */
	get voice() {
		return this.rawMessage.voice;
	}
}

/***********
 * Exports *
 ***********/

module.exports = VoiceMessage;
